import React from "react";
import { MenuItem, TextField } from "@material-ui/core";

export class Form extends React.Component {
    state = {
        name: '',
        age: '',
        gender: '',
        comment: ''
    }

    handleOnNameChange = (event) => {
        this.setState({ name: event.target.value})
    }

    render() {
        return <form noValidate autoComplete="off" style={{ display: 'flex', flexDirection: 'column' }}>
            <TextField value={this.state.name} label="Name" onChange={this.handleOnNameChange} />
            <TextField value={this.state.age} label="Age" type="number" />
            <TextField 
                value={this.state.gender}
                select 
                label = "Select"

            >
                <MenuItem>Male</MenuItem>
                <MenuItem>Female</MenuItem>
                <MenuItem>Different</MenuItem>
            </TextField>
            <TextField
                value={this.state.comment}
                labe="Comment"
                multiline
            />
        </form>;
    }
}
