import React from "react";

class Game extends React.Component {
    state = {
        points: 0
    }

    increment() {
        this.setState({ 
            points: this.state.points + 5 
        })
    }

    decrement() {
        this.setState({ 
            points: this.state.points - 5 
        })
    }

    render() {
        return <>
            <h2> Welcome in Game {this.props.name} !</h2>
            <h3>Your points: {this.state.points}</h3>
            <button onClick={this.increment}>+</button>
            <button onClick={this.decrement}>-</button>
        </>
    }
}

export default Game;
